document.querySelectorAll("form.filter-form, form.sort-form").forEach(function(e){e.addEventListener("submit",function(e){e.preventDefault();var t,o,e=document.querySelector("form.filter-form"),r=document.querySelector("form.sort-form"),n=new FormData(e);for([t,o]of new FormData(r).entries())n.append(t,o);fetch("/wp-admin/admin-post.php",{method:"POST",body:n}).then(e=>e.json()).then(e=>{console.log(e.data);let o=document.querySelector(".taxonomy-items");o.innerHTML="",e.data.forEach(e=>{var t=document.createElement("div");t.classList.add("tech_item"),t.innerHTML=`
                    <h2>${e.title}</h2>
                    <p>Бренд: ${e.brand}</p>
                    <p>Цена: ${e.price}</p>
                    <p>Дата выпуска: ${e.release_date}</p>
                    <a class="button" href="${e.link}">Подробнее</a>
                `,o.appendChild(t)})}).catch(e=>console.error("Ошибка:",e))})});